import React, { useCallback, useEffect, useState } from "react";
import { fetchLeads } from "../api/api";
import { useDashboardFilters } from "../hooks/useDashboardFilters";
import KPICards from "./KPICards";
import Charts from "./Charts";
import LeadsTable from "./LeadsTable";
import LeadModal from "./LeadModal";

function toTimestamp(value) {
  if (!value) return 0;
  const date = new Date(value);
  return Number.isNaN(date.getTime()) ? 0 : date.getTime();
}

const Dashboard = () => {
  const [leads, setLeads] = useState([]);
  const [aggregations, setAggregations] = useState(null);
  const [total, setTotal] = useState(0);
  const [limit, setLimit] = useState(10);
  const [offset, setOffset] = useState(0);
  const { filters, setFilters } = useDashboardFilters();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedLead, setSelectedLead] = useState(null);

  useEffect(() => {
    window.localStorage.removeItem("leadDashboard.filters.v1");
  }, []);

  const loadData = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const requestParams = {
        quality: filters.quality,
        search: filters.search,
        from: filters.from || undefined,
        to: filters.to || undefined
      };

      const firstPageData = await fetchLeads({
        limit,
        offset,
        ...requestParams
      });

      const resolvedTotal = firstPageData.total ?? 0;
      const newestFirstOffset = Math.max(resolvedTotal - offset - limit, 0);

      const pageData =
        newestFirstOffset === offset
          ? firstPageData
          : await fetchLeads({
              limit,
              offset: newestFirstOffset,
              ...requestParams
            });

      const sortedLeads = [...(pageData.leads || [])].sort(
        (a, b) => toTimestamp(b.datetime) - toTimestamp(a.datetime)
      );

      setLeads(sortedLeads);
      setAggregations(pageData.aggregations || firstPageData.aggregations || null);
      setTotal(resolvedTotal);
    } catch (err) {
      // eslint-disable-next-line no-console
      console.error(err);
      setError(
        "Не удалось загрузить данные. Проверьте REACT_APP_API_URL и доступность Apps Script."
      );
    } finally {
      setLoading(false);
    }
  }, [limit, offset, filters.quality, filters.search, filters.from, filters.to]);

  useEffect(() => {
    loadData();
  }, [loadData]);

  const handlePageChange = useCallback((newOffset) => {
    setOffset(newOffset);
  }, []);

  const handlePageSizeChange = useCallback((newLimit) => {
    setLimit(newLimit);
    setOffset(0);
  }, []);

  const handleFilterChange = useCallback((nextFilters) => {
    setFilters(nextFilters);
    setOffset(0);
  }, [setFilters]);

  const handleViewLead = useCallback((lead) => {
    setSelectedLead(lead);
  }, []);

  const handleCloseModal = useCallback(() => {
    setSelectedLead(null);
  }, []);

  return (
    <div className="space-y-4">
      {error && (
        <div className="rounded-md border border-rose-200 bg-rose-50 px-4 py-3 text-sm text-rose-700">
          {error}
        </div>
      )}

      <KPICards kpi={aggregations?.kpi} loading={loading} />
      <Charts aggregations={aggregations} loading={loading} />
      <LeadsTable
        leads={leads}
        total={total}
        limit={limit}
        offset={offset}
        onPageChange={handlePageChange}
        onPageSizeChange={handlePageSizeChange}
        onFilterChange={handleFilterChange}
        filters={filters}
        onViewLead={handleViewLead}
        loading={loading}
      />

      {selectedLead && (
        <LeadModal lead={selectedLead} onClose={handleCloseModal} />
      )}
    </div>
  );
};

export default Dashboard;
