import React from "react";
import { ResponsiveContainer, PieChart, Pie, Cell, Tooltip } from "recharts";

const CHART_COLORS = ["#2563eb", "#10b981", "#ff6b57", "#f59e0b", "#7c3aed"];
const OTHERS_COLOR = "#d1d5db";

const QUALITY_LABELS = {
  высокий: "High",
  хороший: "Good",
  средний: "Medium",
  низкий: "Low",
};

const CARD_COPY = {
  quality: {
    title: "Lead Quality",
    subtitle: "Quality Distribution Overview",
    body: "This graph visualizes incoming leads by warmth level. Focusing on 'High' and 'Good' segments optimizes sales team efforts and increases final deal conversion rates.",
  },
  model: {
    title: "Vehicle Models",
    subtitle: "Demand Structure",
    body: "Reflects current customer interest in the model lineup. Data helps adjust marketing budgets and inventory towards the most sought-after positions.",
  },
  city: {
    title: "Cities",
    subtitle: "Geographic Focus",
    body: "Illustrates the concentration of demand across key territories. Essential for targeting localized campaigns and managing regional resource allocation.",
  },
};

function normalizeQuality(byQuality) {
  return (byQuality || [])
    .map((item) => ({
      name: QUALITY_LABELS[item.quality] || item.quality || "Unknown",
      value: item.count ?? item.value ?? 0,
    }))
    .filter((item) => item.value > 0);
}

function toTopFiveWithOthers(items, nameKey, valueKey) {
  const normalized = (items || [])
    .map((item) => ({
      name: item[nameKey] || item.name || "—",
      value: item[valueKey] ?? item.count ?? item.value ?? 0,
    }))
    .filter((item) => item.value > 0)
    .sort((a, b) => b.value - a.value);

  const topFive = normalized.slice(0, 5);
  const othersValue = normalized.slice(5).reduce((sum, item) => sum + item.value, 0);

  if (othersValue > 0) {
    topFive.push({ name: "Others", value: othersValue, isOthers: true });
  }

  return topFive;
}

function formatCompact(value) {
  return new Intl.NumberFormat("en-US", { notation: "compact" }).format(value || 0);
}

const TooltipContent = ({ active, payload }) => {
  if (!active || !payload || !payload.length) return null;

  const item = payload[0]?.payload;
  if (!item) return null;

  return (
    <div className="rounded-md border border-slate-200 bg-white px-2 py-1 text-xs text-slate-700 shadow-sm">
      <p className="font-medium">{item.name}</p>
      <p>{item.value}</p>
    </div>
  );
};

const renderCalloutLabel = ({ cx, cy, midAngle, outerRadius, percent, name }) => {
  if (!percent || percent <= 0) return null;

  const RADIAN = Math.PI / 180;
  const radius = outerRadius + 18;
  const x = cx + radius * Math.cos(-midAngle * RADIAN);
  const y = cy + radius * Math.sin(-midAngle * RADIAN);
  const textAnchor = x > cx ? "start" : "end";

  return (
    <text
      x={x}
      y={y}
      fill="#334155"
      textAnchor={textAnchor}
      dominantBaseline="central"
      style={{ fontSize: 11, fontWeight: 500 }}
    >
      {`${name} ${Math.round(percent * 100)}%`}
    </text>
  );
};

const EmptyState = ({ loading }) => (
  <div className="flex h-full items-center justify-center text-sm text-slate-400">
    {loading ? "Loading data..." : "Insufficient data"}
  </div>
);

const CircularChart = ({ data, loading }) => {
  if (loading || data.length === 0) {
    return <EmptyState loading={loading} />;
  }

  return (
    <ResponsiveContainer width="100%" height="100%">
      <PieChart>
        <Pie
          data={data}
          dataKey="value"
          nameKey="name"
          cx="50%"
          cy="50%"
          innerRadius={52}
          outerRadius={88}
          paddingAngle={2}
          label={renderCalloutLabel}
          labelLine={{ stroke: "#cbd5e1", strokeWidth: 1 }}
        >
          {data.map((entry, index) => (
            <Cell
              key={`${entry.name}-${index}`}
              fill={entry.isOthers ? OTHERS_COLOR : CHART_COLORS[index % CHART_COLORS.length]}
            />
          ))}
        </Pie>
        <Tooltip content={<TooltipContent />} />
      </PieChart>
    </ResponsiveContainer>
  );
};

const InsightBlock = ({ kpi, title, subtitle, body }) => (
  <div className="flex flex-1 flex-col justify-center">
    <p className="text-3xl font-semibold tracking-tight text-slate-900">{kpi}</p>
    <p className="mt-1 text-sm font-semibold text-slate-900">{title}</p>
    <p className="mt-3 text-base font-semibold text-slate-800">{subtitle}</p>
    <p className="mt-2 text-sm leading-6 text-slate-600">{body}</p>
  </div>
);

const ChartCard = ({ data, loading, copy, kpi, className = "" }) => (
  <div
    className={`rounded-2xl border border-slate-100 bg-white p-6 shadow-[0_10px_30px_rgba(15,23,42,0.06)] ${className}`}
  >
    <div className="flex flex-col gap-6 lg:flex-row lg:items-center">
      <div className="h-72 w-full lg:w-[45%]">
        <CircularChart data={data} loading={loading} />
      </div>
      <InsightBlock
        kpi={kpi}
        title={copy.title}
        subtitle={copy.subtitle}
        body={copy.body}
      />
    </div>
  </div>
);

const Charts = ({ aggregations, loading }) => {
  if (!aggregations && !loading) return null;

  const byQuality = normalizeQuality(aggregations?.by_quality || []);
  const byModel = toTopFiveWithOthers(aggregations?.by_model || [], "model", "count");
  const byCity = toTopFiveWithOthers(aggregations?.by_city || [], "name", "value");

  const totalLeads = byQuality.reduce((sum, item) => sum + item.value, 0);
  const qualityPriority = byQuality
    .filter((item) => item.name === "High" || item.name === "Good")
    .reduce((sum, item) => sum + item.value, 0);

  const topModel = byModel[0];
  const topCity = byCity[0];

  const qualityKpi = totalLeads
    ? `${Math.round((qualityPriority / totalLeads) * 100)}%`
    : "0%";

  const modelKpi = topModel
    ? `${topModel.name} · ${Math.round((topModel.value / byModel.reduce((sum, item) => sum + item.value, 0)) * 100)}%`
    : "No data";

  const cityKpi = topCity
    ? `${topCity.name} · ${Math.round((topCity.value / byCity.reduce((sum, item) => sum + item.value, 0)) * 100)}%`
    : "No data";

  return (
    <div className="mb-6 grid gap-6 lg:grid-cols-12">
      <ChartCard
        className="lg:col-span-7"
        data={byQuality}
        loading={loading}
        copy={CARD_COPY.quality}
        kpi={`${qualityKpi} Priority`}
      />

      <ChartCard
        className="lg:col-span-5"
        data={byModel}
        loading={loading}
        copy={CARD_COPY.model}
        kpi={modelKpi}
      />

      <ChartCard
        className="lg:col-span-12"
        data={byCity}
        loading={loading}
        copy={CARD_COPY.city}
        kpi={`${cityKpi} • ${formatCompact(byCity.reduce((sum, item) => sum + item.value, 0))}`}
      />
    </div>
  );
};

export default Charts;
