import React from "react";
import { format } from "date-fns";

const QUALITY_LABEL_CLASS = {
  высокий: "badge badge-high",
  хороший: "badge badge-good",
  средний: "badge badge-medium",
  низкий: "badge badge-low"
};

function formatDateTime(value) {
  if (!value) return "—";
  try {
    const date = new Date(value);
    if (isNaN(date.getTime())) return value;
    return format(date, "dd.MM.yyyy HH:mm");
  } catch {
    return value;
  }
}

// Вспомогательная функция для парсинга текста диалога в массив объектов
const parseDialog = (dialogText) => {
  if (!dialogText) return [];

  // Регулярное выражение ищет паттерн: "ДД.ММ.ГГ ЧЧ-ММ [Имя]: Текст"
  const regex = /(\d{2}\.\d{2}\.\d{2}\s\d{2}-\d{2})\s\[([^\]]+)\]:\s*([\s\S]*?)(?=(?:\d{2}\.\d{2}\.\d{2}\s\d{2}-\d{2}\s\[)|$)/g;

  const messages = [];
  let match;

  while ((match = regex.exec(dialogText)) !== null) {
    // Достаем время (например "10-49") и форматируем в "10:49"
    const timeRaw = match[1].split(" ")[1];
    const timeFormatted = timeRaw.replace("-", ":");

    messages.push({
      id: match[1] + match[2] + Math.random().toString(36).substr(2, 5), // Уникальный ключ для React
      datetime: match[1],
      time: timeFormatted,
      sender: match[2].trim(),
      text: match[3].trim()
    });
  }

  return messages;
};

const LeadModal = ({ lead, onClose }) => {
  if (!lead) return null;

  const qualityClass =
    QUALITY_LABEL_CLASS[lead.client_quality_bucket] ||
    "badge bg-slate-100 text-slate-700";

  // Парсим диалог один раз при рендере
  const parsedMessages = parseDialog(lead.full_dialog);

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-slate-900/40">
      <div className="card w-full max-w-xl max-h-[90vh] overflow-y-auto dark:bg-slate-800 dark:border-slate-700 flex flex-col">

        {/* Шапка модалки */}
        <div className="flex items-center justify-between border-b border-slate-100 px-6 py-4 dark:border-slate-700">
          <div>
            <h2 className="text-lg font-semibold text-slate-900 dark:text-slate-100">
              Карточка лида
            </h2>
            <p className="text-xs text-slate-500 dark:text-slate-400">
              ID: {lead.id} • {formatDateTime(lead.datetime)}
            </p>
          </div>
          <button
            onClick={onClose}
            className="rounded-full border border-slate-200 px-3 py-1 text-xs font-medium text-slate-600 hover:bg-slate-50 dark:border-slate-700 dark:text-slate-200 dark:hover:bg-slate-700"
          >
            Закрыть
          </button>
        </div>

        {/* Тело модалки */}
        <div className="px-6 py-4 space-y-4">
          <div className="flex flex-wrap gap-2 items-center">
            <span className="text-sm font-medium text-slate-700 dark:text-slate-200">
              {lead.name}
            </span>
            <span className={qualityClass}>
              {lead.client_quality_bucket}{" "}
              {lead.client_quality && `(${lead.client_quality})`}
            </span>
            {lead.selected_car && (
              <span className="badge bg-slate-100 text-slate-700 dark:bg-slate-700 dark:text-slate-100">
                {lead.selected_car}
              </span>
            )}
          </div>

          <div className="grid gap-3 sm:grid-cols-2">
            <div>
              <p className="text-xs font-medium text-slate-500 dark:text-slate-400 mb-1">Город</p>
              <p className="text-sm text-slate-800 dark:text-slate-200">{lead.city || "—"}</p>
            </div>
            <div>
              <p className="text-xs font-medium text-slate-500 dark:text-slate-400 mb-1">Источник</p>
              <p className="text-sm text-slate-800 dark:text-slate-200">
                {lead.traffic_source || "—"}
              </p>
            </div>
            <div>
              <p className="text-xs font-medium text-slate-500 dark:text-slate-400 mb-1">Канал</p>
              <p className="text-sm text-slate-800 dark:text-slate-200">
                {lead.messenger || "—"}
              </p>
            </div>
            <div>
              <p className="text-xs font-medium text-slate-500 dark:text-slate-400 mb-1">
                Дилерский центр
              </p>
              <p className="text-sm text-slate-800 dark:text-slate-200">
                {lead.dealer_center || "—"}
              </p>
            </div>
            <div className="sm:col-span-2">
              <p className="text-xs font-medium text-slate-500 dark:text-slate-400 mb-1">Резюме</p>
              <p className="text-sm text-slate-800 dark:text-slate-200 whitespace-pre-wrap">
                {lead.summary_dialog || "—"}
              </p>
            </div>

            {lead.dialog_link && (
              <div className="sm:col-span-2">
                <p className="text-xs font-medium text-slate-500 dark:text-slate-400 mb-1">
                  Ссылка на диалог / CRM
                </p>
                <a
                  href={lead.dialog_link}
                  target="_blank"
                  rel="noreferrer"
                  className="text-sm text-blue-600 hover:underline break-all"
                >
                  {lead.dialog_link}
                </a>
              </div>
            )}

            {(lead.source_system || lead.platform_user_id) && (
              <div className="sm:col-span-2 grid gap-3 sm:grid-cols-2">
                <div>
                  <p className="text-xs font-medium text-slate-500 dark:text-slate-400 mb-1">
                    Система-источник
                  </p>
                  <p className="text-sm text-slate-800 dark:text-slate-200">
                    {lead.source_system || "—"}
                  </p>
                </div>
                <div>
                  <p className="text-xs font-medium text-slate-500 dark:text-slate-400 mb-1">
                    ID в платформе
                  </p>
                  <p className="text-sm text-slate-800 dark:text-slate-200">
                    {lead.platform_user_id || "—"}
                  </p>
                </div>
              </div>
            )}
          </div>

          {/* Обновленный блок: Полный диалог (Чат) */}
          {lead.full_dialog && (
            <details className="group rounded-md border border-slate-200 bg-slate-50 dark:border-slate-700 dark:bg-slate-900/50 open:bg-slate-100 dark:open:bg-slate-800 transition-colors">
              <summary className="cursor-pointer p-3 text-sm font-medium text-slate-700 dark:text-slate-300 hover:bg-slate-100 dark:hover:bg-slate-700/50 rounded-md outline-none">
                <span className="flex items-center gap-2">
                  <svg className="w-4 h-4 transition-transform group-open:rotate-180" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                  </svg>
                  Полный диалог
                </span>
              </summary>

              <div className="p-4 border-t border-slate-200 dark:border-slate-700 max-h-80 overflow-y-auto flex flex-col space-y-4 custom-scrollbar">
                {parsedMessages.length > 0 ? (
                  parsedMessages.map((msg) => {
                    // Проверяем, кто отправитель (ищем слова "Kia", "Bot", "Менеджер" и т.д.)
                    const isManager = msg.sender.toLowerCase().includes("kia") || msg.sender.toLowerCase().includes("менеджер");

                    return (
                      <div 
                        key={msg.id} 
                        className={`flex flex-col max-w-[85%] ${isManager ? "self-end items-end" : "self-start items-start"}`}
                      >
                        {/* Имя отправителя (только для клиента, над сообщением) */}
                        {!isManager && (
                          <span className="text-[11px] font-medium text-slate-500 dark:text-slate-400 mb-1 ml-1">
                            {msg.sender}
                          </span>
                        )}

                        {/* Само сообщение-пузырь */}
                        <div 
                          className={`px-3 py-2 rounded-2xl relative shadow-sm ${
                            isManager 
                              ? "bg-blue-100 text-slate-800 rounded-tr-sm dark:bg-blue-900/50 dark:text-blue-50" 
                              : "bg-white border border-slate-200 text-slate-800 rounded-tl-sm dark:bg-slate-800 dark:border-slate-700 dark:text-slate-200"
                          }`}
                        >
                          <div className="text-sm whitespace-pre-wrap break-words leading-relaxed">
                            {msg.text}
                          </div>

                          {/* Время сообщения */}
                          <div className={`text-[10px] mt-1 text-right ${
                            isManager ? "text-blue-500/80 dark:text-blue-300/60" : "text-slate-400 dark:text-slate-500"
                          }`}>
                            {msg.time}
                          </div>
                        </div>
                      </div>
                    );
                  })
                ) : (
                  // Фолбэк: если регулярка не сработала (нестандартный формат текста), выводим как было
                  <div className="text-sm text-slate-800 dark:text-slate-200 whitespace-pre-wrap">
                    {lead.full_dialog}
                  </div>
                )}
              </div>
            </details>
          )}
        </div>

        {/* Футер модалки */}
        <div className="flex flex-wrap items-center justify-end gap-3 border-t border-slate-100 px-6 py-3 dark:border-slate-700 mt-auto">
          <button
            onClick={onClose}
            className="text-xs text-slate-500 hover:text-slate-700 dark:text-slate-400 dark:hover:text-slate-200"
          >
            Закрыть окно
          </button>
        </div>
      </div>
    </div>
  );
};

export default LeadModal;
