import React from "react";
import {
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  Legend,
} from "recharts";

const QUALITY_COLORS = {
  высокий: "#22c55e",
  хороший: "#16a34a",
  средний: "#facc15",
  низкий: "#f97316",
};

const CITY_COLORS = [
  "#3b82f6",
  "#22c55e",
  "#a855f7",
  "#f59e0b",
  "#06b6d4",
  "#ef4444",
  "#84cc16",
  "#ec4899",
  "#14b8a6",
  "#6366f1",
];

// Изменение под новый API: отдельная прокручиваемая легенда для большого числа городов.
const CityLegend = ({ payload = [] }) => {
  if (!payload.length) return null;

  return (
    <div className="mt-2 max-h-28 space-y-1 overflow-y-auto pr-1 text-xs">
      {payload.map((entry) => (
        <div key={entry.value} className="flex items-center justify-between gap-3 text-slate-600">
          <div className="flex min-w-0 items-center gap-2">
            <span
              className="h-2.5 w-2.5 shrink-0 rounded-full"
              style={{ backgroundColor: entry.color }}
            />
            <span className="truncate">{entry.value}</span>
          </div>
          <span className="shrink-0 text-slate-500">{entry.payload?.value || 0}</span>
        </div>
      ))}
    </div>
  );
};

const ChartArea = ({ loading, dataLength, children }) => (
  <div className="h-80">
    {loading ? (
      <div className="flex h-full items-center justify-center text-sm text-slate-400">
        Загрузка данных...
      </div>
    ) : dataLength === 0 ? (
      <div className="flex h-full items-center justify-center text-sm text-slate-400">
        Недостаточно данных
      </div>
    ) : (
      <ResponsiveContainer width="100%" height="100%">
        {children}
      </ResponsiveContainer>
    )}
  </div>
);

const Charts = ({ aggregations, loading }) => {
  if (!aggregations && !loading) return null;

  const {
    by_quality = [],
    by_city = [],
    by_model = [],
    by_date = [],
  } = aggregations || {};

  // Изменение под новый API: готовим данные городов из aggregations.by_city ({ name, value }).
  const cityData = by_city
    .filter((city) => Number(city.value) > 0)
    .map((city) => ({
      name: city.name || "Не указан",
      value: Number(city.value) || 0,
    }))
    .sort((a, b) => b.value - a.value);

  const modelsData = [...by_model]
    .sort((a, b) => b.count - a.count)
    .slice(0, 10);

  const timeSeriesData = by_date;

  return (
    <div className="space-y-6 mb-6">
      {/* ===================== */}
      {/* TOP BLOCK */}
      {/* ===================== */}
      <div className="grid gap-6 lg:grid-cols-2">
        {/* PIE — Города */}
        <div className="card p-4">
          <h2 className="mb-2 text-sm font-semibold text-slate-700">
            Статистика лидов по городам
          </h2>

          <ChartArea loading={loading} dataLength={cityData.length}>
            <PieChart>
              <Pie
                data={cityData}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="44%"
                innerRadius={45}
                outerRadius={85}
              >
                {cityData.map((entry, index) => (
                  <Cell
                    key={entry.name}
                    fill={CITY_COLORS[index % CITY_COLORS.length]}
                  />
                ))}
              </Pie>
              {/* Изменение: tooltip в формате "город: количество". */}
              <Tooltip formatter={(value, name) => [value, name]} />
              <Legend
                verticalAlign="bottom"
                align="left"
                content={<CityLegend />}
              />
            </PieChart>
          </ChartArea>
        </div>

        {/* BAR — Модели */}
        <div className="card p-4">
          <h2 className="mb-2 text-sm font-semibold text-slate-700">
            Лиды по моделям автомобилей
          </h2>

          <ChartArea loading={loading} dataLength={modelsData.length}>
            <BarChart data={modelsData}>
              <CartesianGrid strokeDasharray="3 3" vertical={false} />
              <XAxis dataKey="model" tick={{ fontSize: 11 }} />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Bar dataKey="count" fill="#22c55e" radius={[4, 4, 0, 0]} />
            </BarChart>
          </ChartArea>
        </div>
      </div>

      {/* ===================== */}
      {/* BOTTOM BLOCK */}
      {/* ===================== */}
      <div className="card p-4">
        <h2 className="mb-3 text-sm font-semibold text-slate-700">
          Статистика лидов (по качеству)
        </h2>

        <ChartArea loading={loading} dataLength={timeSeriesData.length}>
          <BarChart data={timeSeriesData}>
            <CartesianGrid strokeDasharray="3 3" vertical={false} />
            <XAxis dataKey="date" tick={{ fontSize: 11 }} />
            <YAxis allowDecimals={false} />
            <Tooltip />
            <Legend wrapperStyle={{ fontSize: 12 }} />
            <Bar
              dataKey="высокий"
              stackId="a"
              fill={QUALITY_COLORS["высокий"]}
            />
            <Bar
              dataKey="хороший"
              stackId="a"
              fill={QUALITY_COLORS["хороший"]}
            />
            <Bar
              dataKey="средний"
              stackId="a"
              fill={QUALITY_COLORS["средний"]}
            />
            <Bar
              dataKey="низкий"
              stackId="a"
              fill={QUALITY_COLORS["низкий"]}
            />
          </BarChart>
        </ChartArea>
      </div>

      <div className="grid gap-6 lg:grid-cols-2">
        {/* ===================== */}
        {/* СТАТИСТИКА КАЧЕСТВА */}
        {/* ===================== */}
        <div className="card p-4">
          <h2 className="mb-4 text-sm font-semibold text-slate-700">
            Статистика качества
          </h2>

          <div className="space-y-3">
            {by_quality.map((q) => {
              const total = by_quality.reduce((s, i) => s + i.count, 0);
              const percent = total ? (q.count / total) * 100 : 0;

              return (
                <div key={q.quality}>
                  <div className="mb-1 flex justify-between text-sm">
                    <span>{q.quality}</span>
                    <span className="text-slate-500">{q.count}</span>
                  </div>
                  <div className="h-2 w-full rounded bg-slate-100">
                    <div
                      className="h-2 rounded"
                      style={{
                        width: `${percent}%`,
                        background:
                          q.quality === "высокий"
                            ? "#22c55e"
                            : q.quality === "хороший"
                            ? "#16a34a"
                            : q.quality === "средний"
                            ? "#facc15"
                            : "#f97316",
                      }}
                    />
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* ===================== */}
        {/* КЛЮЧЕВЫЕ МЕТРИКИ */}
        {/* ===================== */}
        <div className="card p-4">
          <h2 className="mb-4 text-sm font-semibold text-slate-700">
            Ключевые метрики
          </h2>

          {(() => {
            const total = by_quality.reduce((s, i) => s + i.count, 0);
            const high =
              by_quality.find((i) => i.quality === "высокий")?.count || 0;
            const good =
              by_quality.find((i) => i.quality === "хороший")?.count || 0;

            const conversion = total
              ? Math.round(((high + good) / total) * 100)
              : 0;
            const highPotential = total ? Math.round((high / total) * 100) : 0;

            const bestDay = [...by_date].sort((a, b) => b.total - a.total)[0];

            return (
              <div className="space-y-3 text-sm">
                <div className="flex justify-between">
                  <span>Общая конверсия</span>
                  <strong>{conversion}%</strong>
                </div>
                <div className="flex justify-between">
                  <span>Высокий потенциал</span>
                  <strong>{highPotential}%</strong>
                </div>
                <div className="flex justify-between">
                  <span>Максимум заявок за день</span>
                  <strong>{bestDay?.total || 0}</strong>
                </div>
                <div className="flex justify-between">
                  <span>Лучший день</span>
                  <strong>{bestDay?.date || "—"}</strong>
                </div>
              </div>
            );
          })()}
        </div>
      </div>
    </div>
  );
};

export default Charts;
