import React from "react";
import { ResponsiveContainer, PieChart, Pie, Cell, Tooltip } from "recharts";

const CHART_COLORS = ["#2563eb", "#10b981", "#ff6b57", "#f59e0b", "#7c3aed"];
const OTHERS_COLOR = "#d1d5db";

const QUALITY_ORDER = ["высокий", "хороший", "средний", "низкий"];
const QUALITY_LABELS = {
  высокий: "Высокий",
  хороший: "Хороший",
  средний: "Средний",
  низкий: "Низкий",
};

const CARD_COPY = {
  quality: {
    subtitle: "Качество лидов",
    title: "Общий срез качества",
    body: "График показывает распределение поступивших заявок по уровню теплоты. Фокус на сегментах 'Высокий' и 'Хороший' позволяет оптимизировать работу отдела продаж и повысить итоговую конверсию в сделку.",
  },
  model: {
    subtitle: "Модели автомобилей",
    title: "Структура спроса",
    body: "Отражает актуальный интерес клиентов к модельному ряду. Данные помогают корректировать маркетинговые бюджеты и складские запасы в пользу наиболее востребованных позиций.",
  },
  city: {
    subtitle: "Статистика по городам",
    title: "География спроса",
    body: "Показывает концентрацию спроса по ключевым территориям. Это необходимо для таргетирования локальных кампаний и управления региональными ресурсами.",
  },
};

const STATIC_METRICS = [
  { label: "Общая конверсия", value: "55%" },
  { label: "Высокий потенциал", value: "49%" },
  { label: "Максимум заявок за день", value: "161" },
  { label: "Лучший день", value: "2026-02-24" },
];

function normalizeQuality(byQuality) {
  const normalized = (byQuality || []).map((item) => ({
    quality: item.quality,
    name: QUALITY_LABELS[item.quality] || item.quality || "Неизвестно",
    value: item.count ?? item.value ?? 0,
  }));

  return normalized
    .filter((item) => item.value > 0)
    .sort((a, b) => QUALITY_ORDER.indexOf(a.quality) - QUALITY_ORDER.indexOf(b.quality));
}

function toTopFiveWithOthers(items, nameKey, valueKey) {
  const normalized = (items || [])
    .map((item) => ({
      name: item[nameKey] || item.name || "—",
      value: item[valueKey] ?? item.count ?? item.value ?? 0,
    }))
    .filter((item) => item.value > 0)
    .sort((a, b) => b.value - a.value);

  const topFive = normalized.slice(0, 5);
  const othersValue = normalized.slice(5).reduce((sum, item) => sum + item.value, 0);

  if (othersValue > 0) {
    topFive.push({ name: "Остальные", value: othersValue, isOthers: true });
  }

  return topFive;
}

const TooltipContent = ({ active, payload }) => {
  if (!active || !payload || !payload.length) return null;

  const item = payload[0]?.payload;
  if (!item) return null;

  return (
    <div className="rounded-md border border-slate-200 bg-white px-2 py-1 text-xs text-slate-700 shadow-sm">
      <p className="font-medium">{item.name}</p>
      <p>{item.value}</p>
    </div>
  );
};

const renderCalloutLabel = ({ cx, cy, midAngle, outerRadius, percent, name }) => {
  if (!percent || percent <= 0) return null;

  const RADIAN = Math.PI / 180;
  const labelRadius = outerRadius + 24;
  const x = cx + labelRadius * Math.cos(-midAngle * RADIAN);
  const y = cy + labelRadius * Math.sin(-midAngle * RADIAN);
  const textAnchor = x > cx ? "start" : "end";

  return (
    <text
      x={x}
      y={y}
      fill="#334155"
      textAnchor={textAnchor}
      dominantBaseline="central"
      style={{ fontSize: 11, fontWeight: 500 }}
    >
      {`${name} ${Math.round(percent * 100)}%`}
    </text>
  );
};

const EmptyState = ({ loading }) => (
  <div className="flex h-full items-center justify-center text-sm text-slate-400">
    {loading ? "Загрузка данных..." : "Недостаточно данных"}
  </div>
);

const CircularChart = ({ data, loading }) => {
  if (loading || data.length === 0) {
    return <EmptyState loading={loading} />;
  }

  return (
    <div className="h-full w-full overflow-visible rounded-xl bg-white p-6">
      <ResponsiveContainer width="100%" height="100%">
        <PieChart margin={{ top: 30, right: 64, bottom: 30, left: 64 }}>
          <Pie
            data={data}
            dataKey="value"
            nameKey="name"
            cx="50%"
            cy="50%"
            innerRadius={28}
            outerRadius={48}
            paddingAngle={2}
            label={renderCalloutLabel}
            labelLine={{ stroke: "#cbd5e1", strokeWidth: 1 }}
          >
            {data.map((entry, index) => (
              <Cell
                key={`${entry.name}-${index}`}
                fill={entry.isOthers ? OTHERS_COLOR : CHART_COLORS[index % CHART_COLORS.length]}
              />
            ))}
          </Pie>
          <Tooltip content={<TooltipContent />} />
        </PieChart>
      </ResponsiveContainer>
    </div>
  );
};

const InsightBlock = ({ subtitle, title, body }) => (
  <div className="flex flex-1 flex-col justify-center">
    <p className="text-sm font-semibold tracking-wide text-slate-500">{subtitle}</p>
    <p className="mt-2 text-2xl font-semibold tracking-tight text-slate-900">{title}</p>
    <p className="mt-3 text-sm leading-6 text-slate-600">{body}</p>
  </div>
);

const DonutCard = ({ data, loading, copy, className = "" }) => (
  <div className={`rounded-2xl border border-slate-100 bg-white p-6 shadow-[0_10px_30px_rgba(15,23,42,0.06)] ${className}`}>
    <div className="flex h-72 flex-col gap-5 md:flex-row md:items-center">
      <div className="h-full w-full md:w-2/5">
        <CircularChart data={data} loading={loading} />
      </div>
      <div className="md:w-3/5">
        <InsightBlock subtitle={copy.subtitle} title={copy.title} body={copy.body} />
      </div>
    </div>
  </div>
);

const KeyMetricsCard = ({ className = "" }) => (
  <div className={`rounded-2xl border border-slate-100 bg-white p-6 shadow-[0_10px_30px_rgba(15,23,42,0.06)] ${className}`}>
    <h2 className="mb-4 text-sm font-semibold text-slate-700">Ключевые метрики</h2>
    <div className="flex flex-wrap gap-4 text-sm xl:flex-nowrap">
      {STATIC_METRICS.map((metric) => (
        <div key={metric.label} className="min-w-[180px] flex-1 rounded-xl border border-slate-100 bg-slate-50/70 px-4 py-3">
          <p className="text-xs text-slate-500">{metric.label}</p>
          <p className="mt-1 text-lg font-semibold text-slate-900">{metric.value}</p>
        </div>
      ))}
    </div>
  </div>
);

const Charts = ({ aggregations, loading }) => {
  if (!aggregations && !loading) return null;

  const byQuality = normalizeQuality(aggregations?.by_quality || []);
  const byModel = toTopFiveWithOthers(aggregations?.by_model || [], "model", "count");
  const byCity = toTopFiveWithOthers(aggregations?.by_city || [], "name", "value");

  return (
    <div className="mb-6 space-y-6 font-sans">
      <KeyMetricsCard />

      <DonutCard
        data={byQuality}
        loading={loading}
        copy={CARD_COPY.quality}
      />

      <DonutCard
        data={byModel}
        loading={loading}
        copy={CARD_COPY.model}
      />

      <DonutCard
        data={byCity}
        loading={loading}
        copy={CARD_COPY.city}
      />
    </div>
  );
};

export default Charts;
